        def makeLadders(rows):
            # rows = (int(input("명 수를 입력하세요.>>> ")))

            # 세로줄과 개행문자 생성(len = (rows+2)*14)
            draw_rows = ("| " * rows + "\n") * 14

            # 빈 칸이나 _가 들어갈 공간(space) 배정(draw_rows 의 공백 위치)
            space = []
            for i in range(len(draw_rows)):
                if draw_rows[i] == " " and draw_rows[i + 1] != "\n":
                    space.append(i)

            # space 에 포함된 위치이면 ' '나 '_'을 replace
            Ladders = ""
            for i in range(len(draw_rows)):
                if i in space:
                    randomBit = random.getrandbits(1)
                    if draw_rows[i + 1] == "\n":
                        Ladders += ' '
                    elif i > 2 and Ladders[i - 2] != '_' and randomBit == 0 and i < (len(draw_rows) - (2 * rows + 2)):
                        Ladders += '_'
                    else:
                        Ladders += ' '
                else:
                    Ladders += (draw_rows[i])
            for j in range(rows):
                Ladders += str(j + 1) + ' '

            return Ladders

        self.runButton.clicked.connect(lambda: self.ladderDisplay.setText(makeLadders(rows=int(self.numEdit.value()))))

        def Answer(n, res):
            # n = self.numEdit.value()
            # res = runBtn number
            x = 2*(res - 1)
            Ladder = self.ladderDisplay.text()
            while x <= (len(Ladder) - 2*n-2):
                if Ladder[x-1] == '_':
                    x = x + 2*n - 1
                elif Ladder[x+1] == '_':
                    x = x + 2*n + 3
                else:
                    x = x + 2*n + 1
            x = (x - 14*(2*n + 1) + 2)/2
            return x
